import React from "react";
import Chart from "react-apexcharts";
import axios from "axios";

const colors = ["#008FFB", "#00E396", "#FEB019", "#FF4560", "#775DD0"];
class SalesPerSegment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      options: {
        plotOptions: {
          bar: {
            horizontal: false,
            endingShape: "rounded",
            columnWidth: "55%",
          },
        },
        dataLabels: {
          enabled: false,
        },
        stroke: {
          show: true,
          width: 2,
          colors: ["transparent"],
        },
        xaxis: {
          categories: ["Q1", "Q2", "Q3", "Q4"],
        },
        yaxis: {
          title: {
            text: "$ (thousands)",
          },
        },
        fill: {
          opacity: 1,
        },
        tooltip: {
          y: {
            formatter: function (val) {
              return "$ " + val + " thousands";
            },
          },
        },
        legend: {
          offsetY: -10,
        },
      },
      series: [
        {
          name: "Big Spenders",
          data: [44, 55, 57, 56],
        },
        {
          name: "Registered",
          data: [76, 85, 101, 98],
        },
        {
          name: "VIP Shoppers",
          data: [56, 35, 45, 20],
        },
        {
          name: "Coupon Lovers",
          data: [100, 60, 45, 34],
        },
        {
          name: "Guest",
          data: [43, 20, 80, 90],
        },
      ],
    };
  }
  componentDidMount() {
    var token = localStorage.getItem("auth");
    axios
      .get("http://202.143.121.156:3031/quarterly_cluster_sales", {
        headers: { Authorization: "Bearer " + token },
      })
      .then((response) => {
        if (response.status === 200 && response != null) {
          console.log(response.data);
          let my_series = [
            {
              data: [],
            },
          ];

          response.data.forEach((item) => {
            my_series[0].data.push(item.value);
          });
          //   console.log(my_series);
          this.setState({
            series: my_series,
            // options: my_options,
          });
        } else {
          console.log("problem");
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    return (
      <div id="chart" className="apexcharts-content">
        <Chart
          options={this.state.options}
          series={this.state.series}
          type="bar"
          height={350}
        />
      </div>
    );
  }
}

export default SalesPerSegment;
