import "./styles.css";

import React from "react";
import classNames from "./styles.css";
import { useState } from "react";
import FunctionFillIcon from "remixicon-react/FunctionFillIcon";
import ArrowRightSLineIcon from "remixicon-react/ArrowRightSLineIcon";
import GroupLineIcon from "remixicon-react/GroupLineIcon";
import CalendarEventLineIcon from "remixicon-react/CalendarEventLineIcon";
import PriceTag2LineIcon from "remixicon-react/PriceTagLineIcon";
import MoreLineIcon from "remixicon-react/MoreLineIcon";
import CloseLineIcon from "remixicon-react/CloseLineIcon";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import NaturalDragAnimation from "natural-drag-animation-rbdnd";
import TextareaAutosize from "react-textarea-autosize";

function getStyle(style, snapshot) {
  if (!snapshot.isDropAnimating) {
    return style;
  }
  return {
    ...style,
    // cannot be 0, but make it super tiny
    transitionDuration: `0.0001s`
  };
}
const Button = () => {
  return (
    <button className="button">
      <img src={"https://media.istockphoto.com/photos/hands-forming-a-heart-shape-with-sunset-silhouette-picture-id636379014?k=20&m=636379014&s=612x612&w=0&h=Zb6EobVr_BR278tpANKvKzfcQMIz-P_2Tv3XVXZufAM="} alt="icon" /> Add Task
    </button>
  );
};
const Card = (props) => {
  return props.button ? (
    <div className="button" onClick={props.onClick}>
      <img src={"https://media.istockphoto.com/photos/hands-forming-a-heart-shape-with-sunset-silhouette-picture-id636379014?k=20&m=636379014&s=612x612&w=0&h=Zb6EobVr_BR278tpANKvKzfcQMIz-P_2Tv3XVXZufAM="} alt="icon" /> Add Task
    </div>
  ) : (
    <div className="card" style={props.style}>
      {props.img ? (
        <img src={props.img} className="card__img" alt="img" />
      ) : null}
      <div style={{ padding: "16px" }}>
        <div className="card__top-bar">
          <span className="card__title">{props.title}</span>
          <img className="card__check" alt="icon" src={checkIcon} />
        </div>
        <span className="card__subtitle">{props.subtitle}</span>
        <div className="card__middle-bar">
          <div className="card__tags">
            {props.tags
              ? props.tags.map((tag) => {
                  return (
                    <div
                      className="card__tag"
                      style={{
                        background: tag.colorBg,
                        color: tag.colorFg
                      }}
                    >
                      {tag.name}
                    </div>
                  );
                })
              : null}
          </div>
          <div className="card__users">
            {props.users
              ? props.users.map((image) => {
                  return (
                    <img className="card__user" alt="avatar" src={image} />
                  );
                })
              : null}
          </div>
        </div>
      </div>
      <div className="card__bottom-bar">
        <div className="card__bottom-bar__left">
          {props.comments === 0 ? null : (
            <div className="card__comment">
              <img
                className="card__comment__icon icon"
                alt="icon"
                src={commentIcon}
              />
              <span className="card__comment__count">{props.comments}</span>
            </div>
          )}

          {props.links === 0 ? null : (
            <div className="card__link">
              <img
                className="card__link__icon icon"
                alt="icon"
                src={linkIcon}
              />
              <span className="card__link__count">{props.links}</span>
            </div>
          )}
        </div>

        <div className="card__bottom-bar__right">
          {props.date === 0 ? null : (
            <div className="card__date">
              <img
                className="card__date__icon icon"
                alt="icon"
                src={calendarIcon}
              />
              <span className="card__date__text">{props.date}</span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const itemsFromBackend = [
  {
    id: "1",
