      },
      { id: "6", content: "Second task", button: true }
    ]
  },
  c: {
    name: "In Progress",
    items: [
      {
        id: "7",
        img: cardImg2,
        title: "Build real-time chat",
        subtitle: (
          <span>
            Build the secure and HIPAA, Soc2, GDPR ready chat messaging
          </span>
        ),
        tags: [
          {
            name: "Improvement",
            colorBg: "#F3FBE8",
