          }
        ],
        users: [user1],
        comments: 8,
        links: 2,
        date: "June 6-7, 2022"
      },
      { id: "10", content: "Second task", button: true }
    ]
  }
};

const onDragEnd = (result, columns, setColumns) => {
  if (!result.destination) return;
  const { source, destination } = result;

  if (source.droppableId !== destination.droppableId) {
    const sourceColumn = columns[source.droppableId];
    const destColumn = columns[destination.droppableId];
    const sourceItems = [...sourceColumn.items];
    const destItems = [...destColumn.items];
    const [removed] = sourceItems.splice(source.index, 1);
    destItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...sourceColumn,
        items: sourceItems
      },
      [destination.droppableId]: {
        ...destColumn,
        items: destItems
      }
    });
  } else {
    const column = columns[source.droppableId];
    const copiedItems = [...column.items];
    const [removed] = copiedItems.splice(source.index, 1);
    copiedItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...column,
        items: copiedItems
      }
    });
  }
};

function App() {
  const [columns, setColumns] = useState(columnsFromBackend);
  const [modal, showModal] = useState(false);
  const [user, showUser] = useState(false);
  const [final, activeFinal] = useState(false);
  return (
    <div className="App">
      <DragDropContext
        onDragEnd={(result) => onDragEnd(result, columns, setColumns)}
      >
        <div className="grid">
          {Object.entries(columns).map(([columnId, column], index) => {
            return (
              <div key={columnId}>
                <div>
                  <Droppable droppableId={columnId} key={columnId}>
                    {(provided, snapshot) => {
                      return (
                        <div
                          {...provided.droppableProps}
                          ref={provided.innerRef}
                          // style={{
                          //   background: snapshot.isDraggingOver
                          //     ? "lightblue"
                          //     : "lightgrey",
                          //   padding: 4,
                          //   width: 250,
                          //   minHeight: 500
                          // }}
                          className="column"
                          style={{
                            borderRight: `${
                              columnId == "d" ? "none" : "1px solid #efefef"
                            }`
                          }}
                        >
                          {(columnId !== "b") & (columnId !== "c") ? null : (
                            <div
                              className="card"
                              style={{
                                width: "24px",
                                height: "28px",
                                display: "flex",
                                justifyContent: "center",
                                alignItems: "center",
                                fontSize: "12px",
                                fontWeight: "500",
                                color: "#686F7A",
                                marginTop: "-58px",
                                marginLeft:
                                  columnId === "c" ? "-120px" : "-93px",
                                position: "absolute"
                              }}
                            >
                              {column.items.length - 1}
                            </div>
                          )}

                          {snapshot.isDraggingOver ? (
                            <div className="placeholder"></div>
                          ) : null}
                          {column.items.map((item, index) => {
                            return (
                              <Draggable
                                key={item.id}
                                draggableId={item.id}
                                index={index}
                              >
                                {(provided, snapshot) => {
                                  return (
                                    <NaturalDragAnimation
                                      style={getStyle(
                                        provided.draggableProps.style,
                                        snapshot
                                      )}
                                      snapshot={snapshot}
                                    >
                                      {(style) => (
                                        <div
                                          ref={provided.innerRef}
                                          {...provided.draggableProps}
                                          {...provided.dragHandleProps}
                                          style={style}
                                        >
                                          <Card
                                            id="2"
                                            img={item.img}
                                            button={item.button}
                                            title={item.title}
                                            subtitle={item.subtitle}
                                            tags={item.tags}
                                            users={item.users}
                                            comments={item.comments}
                                            links={item.links}
                                            date={item.date}
                                            onClick={() => {
                                              showModal(true);
                                            }}
                                          />
                                        </div>
                                      )}
                                    </NaturalDragAnimation>
                                  );
                                }}
                              </Draggable>
                            );
                          })}
                          {provided.placeholder}
                        </div>
                      );
                    }}
                  </Droppable>
                </div>
              </div>
            );
          })}
        </div>
      </DragDropContext>
      <div
        className="overlay"
        style={{
          opacity: modal ? "0.5" : "0.0",
          pointerEvents: modal ? "auto" : "none"
        }}
        onClick={() => {
          showModal(false);
        }}
      ></div>
      <div
        className="modal"
        style={{
          opacity: modal ? "1" : "0.0",
          pointerEvents: modal ? "auto" : "none"
        }}
      >
        <div className="modal__top">
          <div
            style={{
              display: "flex",
              gap: "8px",
              alignItems: "center"
