          <div
            className={`tag ${final ? "tag--active" : null}`}
            style={{
              background: user ? "#fbfbfb" : null
            }}
            onClick={() => {
              showUser(!user);
            }}
          >
            {final ? (
              <img src={u4} height="20px" />
            ) : (
              <GroupLineIcon size={16} color="#B9BCC2" />
            )}
            {final ? "Cristofer Philips" : "Assignee"}
          </div>
          <div className="tag">
            <CalendarEventLineIcon size={16} color="#B9BCC2" />
            {final ? "" : "Date"}
          </div>
          <div className="tag">
            <PriceTag2LineIcon size={16} color="#B9BCC2" />
            {final ? "" : "Label"}
          </div>
          <MoreLineIcon size={24} color="#979CA4" />
          <div
            style={{
              marginLeft: "auto"
            }}
            className="create"
          >
            Create
          </div>
        </div>

        <div
          className="dropdown"
          style={{
            opacity: user ? "1" : "0",
            transition: "all 0.15s linear"
          }}
        >
          <div
            className="dropdown-item"
            style={{
              marginTop: "8px",
              marginBottom: "8px",
              justifyContent: "space-between"
            }}
          >
            <span
              style={{
                opacity: 0.5
              }}
            >
              Assign to...
            </span>
            <div
              className="modal__project"
              style={{
                width: "24px",
                boxSizing: "border-box",
                display: "flex",
                justifyContent: "center",
                height: "22px"
              }}
            >
              <span
                className="modal__project__text"
                style={{
                  fontSize: "10px"
                }}
              >
                A
              </span>
            </div>
          </div>
          <div className="dropdown-item">
            <img src={u5} height="20px" />
            Unassigned
          </div>
          <div
            className="dropdown-item"
            onClick={() => {
              showUser(false);
              activeFinal(true);
            }}
          >
            <img src={u4} height="20px" />
            Cristofer Philips
          </div>
          <div className="dropdown-item">
            <img src={u2} height="20px" />
            Marcus Torff
          </div>
          <div className="dropdown-item">
            <img src={u3} height="20px" />
            Nolan Siphron
          </div>
          <div className="dropdown-item">
            <img src={u1} height="20px" />
            Corey Philips
          </div>
        </div>
      </div>
    </div>
  );
}

